mapper.select:
select count(*) from activity


        and channel = #{channel}


        and type = #{type}



            AND start_time > NOW()


            AND start_time <= NOW() AND end_time >= NOW()


            AND end_time < NOW()

select * from activity


        and channel = #{channel}


        and type = #{type}



            AND start_time > NOW()


            AND start_time <= NOW() AND end_time >= NOW()


            AND end_time < NOW()



order by update_time desc
limit #{offset},#{pageSize}
