mapper.select:
select t1.*, t2.name as assign_name from clue as t1
left join user as t2
on t1.user_id = t2.id


        and t1.id = #{clueQueryDTO.clueId}


        and t1.phone like concat('%', #{clueQueryDTO.phone}, '%')


        and t1.channel = #{clueQueryDTO.channel}


        and t2.name = #{clueQueryDTO.assignName}


        and t1.status = #{clueQueryDTO.status}

    and t1.status in (1, 2, 3)

order by t1.update_time desc

select t1.*, t2.name as activity_name from clue as t1 left join activity as t2 on t1.activity_id = t2.id
and t1.id = #{cluePoolQueryDTO.clueId}
and t1.phone like concat('%', #{cluePoolQueryDTO.phone}, '%')
and t1.channel = #{cluePoolQueryDTO.channel}
and status = 4
order by t1.update_time desc
