mapper.delete:
delete from user
where id in

    #{id}

mapper.select:
select count(*) from user


        and name like concat('%',#{name},'%')


        and status = #{status}


        and phone like concat('%',#{phone},'%')


        and dept_id = #{deptId}

select u.id, u.name, u.username, u.password, u.phone, u.email, u.gender, u.status, u.image, u.remark, u.create_time, u.update_time, d.name as dept_name, r.name as role_name from user u left join dept d on u.dept_id = d.id left join role r on u.role_id = r.id
and name like concat('%',#{name},'%')
and status = #{status}
and phone like concat('%',#{phone},'%')
and dept_id = #{deptId}
order by update_time desc limit #{page}, #{pageSize}
